    }

    render(){
        return(

            <div className="col-sm-6">

                <div className="card" >
                    <Link to={`/details/${this.props.device.id}`}>
                        <img class="card-img-top" src={this.props.device.url} alt="Card image cap"/>
                    </Link>    
                        <div className="card-body">
                            <h5 className="card-title">{this.props.device.name}</h5>
                            <button className="btn btn-primary" onClick={()=>{this.addToCart(this.props.device.id)}}>
                            <span class="material-icons">add_shopping_cart</span>
                            Add to Cart
                            </button>
                            <h5 className="card-title">Prize :{this.props.device.prize}</h5>

                        </div>
                </div>
                    <br/>

            </div>


        );
    }
}

export default Card;
