import React,{Component} from 'react';
import {ContextConsumer} from '../context/Context';

class Cart extends Component{

    render(){
        return(
            <table class="table table-striped table-dark">
                <thead>
                    <tr>
                    <th scope="col">#</th>
                    <th scope="col">Name</th>
                    <th scope="col">Prize</th>
                    <th scope="col">No Of Items</th>
                    </tr>
                </thead>
                <tbody>
                    <ContextConsumer>
                        {(data)=>{
                            let temp=data.devices.filter((item)=>{return item.added===true; })

                            return(temp.map(item=>{
                                            return(
                                                <tr key={item.id}>
                                                <th scope="row">{temp.indexOf(item)+1}</th>
                                                <td>{item.name}</td>
                                                <td>{item.prize}</td>
                                                <td>{item.count}</td>
                                                <td><button type="button" class="btn btn-success" onClick={()=>addItem}>Item +</button></td>
                                                <td><button type="button" class="btn btn-warning" on>Item -</button></td>
                                                <td><button type="button" class="btn btn-danger">Remove</button></td>
                                                </tr>)
                                            }))
                        }}
                    </ContextConsumer>
                </tbody>
            </table>
        )
    }
}

export default Cart;
